  @Test
  public void greaterThanLength() {
    LinkedList testList = new LinkedList();
    testList.insert("N");
    testList.insert("A");
    testList.insert("I");
    testList.insert("M");
    assertEquals("Exception", testList.kth(5));
  }

  @Test
  public void equalThanLength() {
    LinkedList testList = new LinkedList();
    testList.insert("N");
    testList.insert("A");
    testList.insert("I");
    testList.insert("M");
    assertEquals("Exception", testList.kth(4));
  }

  @Test
  public void negativeIndex() {
    LinkedList testList = new LinkedList();
    testList.insert("N");
    testList.insert("A");
    testList.insert("I");
    testList.insert("M");
    assertEquals("Exception", testList.kth(-1));
  }

  @Test
  public void size1() {
    LinkedList testList = new LinkedList();
    testList.insert("N");
    assertEquals("Exception", testList.kth(1));
  }

